import React, { Component } from 'react'
import { NavLink, useLocation  } from "react-router-dom";

const Menu = () => {
    const location = useLocation();

    // All dashboard routes
    const isCategoryOpen = location.pathname.startsWith("/categories");

    return (
      <div>
        <aside className="main-sidebar sidebar-dark-primary elevation-4">
          {/* Brand Logo */}
          <a href="/" className="brand-link text-center">
            <i className="nav-icon fas fa-money-bill-alt"></i>
            <span className="brand-text font-weight-light ml-2">My money</span>
          </a>
          {/* Sidebar */}
          <div className="sidebar">
            {/* Sidebar Menu */}
            <nav className="mt-2">
              <ul className="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
                <li className="nav-item">
                  <NavLink
                    to="/"
                    end
                    className={({ isActive }) => `nav-link ${isActive ? "active" : ""}`}
                  >
                    <i className="nav-icon fas fa-tachometer-alt" />
                    <p>Dashboard</p>
                  </NavLink>
                </li>

                <li className={`nav-item has-treeview ${isCategoryOpen ? "menu-open" : ""}`}>
                  <NavLink
                    to="/categories"
                    className={({ isActive }) => `nav-link ${isActive ? "active" : ""}`}
                  >
                    <i className="nav-icon fas fa-th" />
                    <p>Categories</p>
                  </NavLink>
                </li>

              </ul>
            </nav>
            {/* /.sidebar-menu */}
          </div>
          {/* /.sidebar */}
        </aside>
      </div>

    )
  }
export default Menu;
