import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import AddTransactionModal from "../components/AddTransactionModal";
import './MainTransaction.css';

const MainTransaction = () => {
  const [showModal, setShowModal] = useState(false);
  const [transactions, setTransactions] = useState([]);
  const [month, setMonth] = useState(new Date().getMonth() + 1);
  const [year, setYear] = useState(new Date().getFullYear());
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);
  const [viewData, setViewData] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/transactions?month=${month}&year=${year}`)
      .then((res) => {
        setTransactions(res.data.transactions);
        setTotalIncome(res.data.totalIncome);
        setTotalExpense(res.data.totalExpense);
      });
  }, [month, year]);

  useEffect(() => {
    fetchCategories();
  }, []);

  return (
    <div className="content-wrapper">
      <section className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-md-6">
              <h1>All transaction</h1>
            </div>
            <div className="col-md-6">
              <ol className="breadcrumb float-sm-right">
                <li className="breadcrumb-item"><a href="/">Home</a></li>
                <li className="breadcrumb-item active">All transactions</li>
              </ol>
            </div>
          </div>
        </div>
      </section>
      <section className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12">
              <div className="card">
                <div className="card-header">
                  <div className='row'>
                    <div className='col-md-6'><h3 className="card-title">List of transactions</h3></div>
                    <div className='col-md-6 text-right'>
                      <button
                        className="btn btn-primary mb-3"
                        onClick={() => setShowModal(true)}
                      >
                        Add Transaction
                      </button>
                      <AddTransactionModal
                        show={showModal}
                        onClose={() => {
                          setShowModal(false);
                          // setEditData(null);
                          // fetchCategories(); // ðŸ”¥ refresh list
                        }}
                        type="expense"
                      // editData={editData}
                      />
                    </div>
                  </div>
                </div>
                <div className="card-body">
                  <select
                    value={month}
                    onChange={(e) => setMonth(e.target.value)}
                  >
                    {Array.from({ length: 12 }, (_, i) => (
                      <option key={i + 1} value={i + 1}>
                        {new Date(0, i).toLocaleString("en", { month: "long" })}
                      </option>
                    ))}
                  </select>
                  <div className="summary">
                    <span className="income">Income: â‚¹{totalIncome}</span>
                    <span className="expense">Expense: â‚¹{totalExpense}</span>
                  </div>
                  <div className="table-responsive">
                    <table className="table table-bordered">
                      <thead>
                        <tr>
                          <th style={{ width: 50 }}>#</th>
                          <th>Transaction type</th>
                          <th>Account type</th>
                          <th>Category</th>
                          <th>Amount</th>
                          <th style={{ width: 150 }}>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {/* {loading ? (
                          <tr>
                            <td colSpan="4" className="text-center">Loading...</td>
                          </tr>
                        ) : categories.length === 0 ? (
                          <tr>
                            <td colSpan="4" className="text-center">No categories found</td>
                          </tr>
                        ) : (
                          categories.map((cat, index) => (
                            <tr key={cat.id}>
                              <td>{index + 1}</td>
                              <td>{cat.category_name}</td>
                              <td>
                                <span
                                  className="badge"
                                  style={{ backgroundColor: cat.category_color, color: "#fff" }}
                                >
                                  {cat.category_color}
                                </span>
                              </td>
                              <td>
                                <button
                                  className="btn btn-sm btn-warning mr-2"
                                  onClick={() => {
                                    setEditData(cat);
                                    setShowModal(true);
                                  }}
                                >
                                  <i className="fas fa-edit"></i>
                                </button>

                                <button
                                  className="btn btn-sm btn-danger"
                                  onClick={() => handleDelete(cat.id)}
                                >
                                  <i className="fas fa-trash"></i>
                                </button>
                              </td>
                            </tr>
                          ))
                        )} */}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>

  )
}
export default MainTransaction;
